<?php/* * To change this license header, choose License Headers in Project Properties. * To change this template file, choose Tools | Templates * and open the template in the editor. *//** * Description of MenuPhoto * * @author chalana */class MenuPhoto {    public $id;    public $menu;    public $image_name;    public $queue;    public function __construct($id) {        if ($id) {            $query = "SELECT * FROM `menu_photo` WHERE `id`=" . $id;            $db = new Database();            $result = mysqli_fetch_array($db->readQuery($query));            $this->id = $result['id'];            $this->menu = $result['menu'];            $this->image_name = $result['image_name'];            $this->queue = $result['queue'];            return $this;        }    }    public function create() {        $query = "INSERT INTO `menu_photo` (`menu`,`image_name`,`queue`) VALUES  ('"                . $this->menu . "','"                . $this->image_name . "', '"                . $this->queue . "')";                     $db = new Database();        $result = $db->readQuery($query);        if ($result) {            return TRUE;        } else {            return FALSE;        }    }    public function all() {        $query = "SELECT * FROM `menu_photo` ORDER BY queue ASC";        $db = new Database();        $result = $db->readQuery($query);        $array_res = array();        while ($row = mysqli_fetch_array($result)) {            array_push($array_res, $row);        }        return $array_res;    }    public function update() {        $query = "UPDATE  `menu_photo` SET "                . "`menu` ='" . $this->menu . "', "                . "`image_name` ='" . $this->image_name . "', "                . "`queue` ='" . $this->queue . "' "                . "WHERE `id` = '" . $this->id . "'";        $db = new Database();        $result = $db->readQuery($query);        if ($result) {            return $this->__construct($this->id);        } else {            return FALSE;        }    }    public function delete() {        unlink(Helper::getSitePath() . "upload/restaurant/menu/" . $this->image_name);        unlink(Helper::getSitePath() . "upload/restaurant/menu/thumb/" . $this->image_name);        $query = 'DELETE FROM `menu_photo` WHERE id="' . $this->id . '"';        $db = new Database();        return $db->readQuery($query);    }    public function getMenuPhotosById($menu) {        $query = "SELECT * FROM `menu_photo` WHERE `menu`= '" . $menu . "'   ORDER BY queue ASC";               $db = new Database();        $result = $db->readQuery($query);        $array_res = array();        while ($row = mysqli_fetch_array($result)) {            array_push($array_res, $row);        }        return $array_res;    }    public function arrange($key, $img) {        $query = "UPDATE `menu_photo` SET `queue` = '" . $key . "'  WHERE id = '" . $img . "'";        $db = new Database();        $result = $db->readQuery($query);        return $result;    }}