<?php/** * Description of TourTypes * * @author U s E r Â¨ */class MenuType {    public $id;    public $restaurant_id;    public $title;    public $queue;    public function __construct($id) {        if ($id) {            $query = "SELECT * FROM `menu_type` WHERE `id`=" . $id;            $db = new Database();            $result = mysqli_fetch_array($db->readQuery($query));            $this->id = $result['id'];            $this->restaurant_id = $result['restaurant_id'];            $this->title = $result['title'];            $this->queue = $result['queue'];            return $this;        }    }    public function create() {        $query = "INSERT INTO `menu_type` (`restaurant_id`,`title`,`queue`) VALUES  ('"                . $this->restaurant_id . "', '"                . $this->title . "', '"                . $this->queue . "')";        $db = new Database();        $result = $db->readQuery($query);        if ($result) {            return mysqli_insert_id($db->DB_CON);        } else {            return FALSE;        }    }    public function all() {        $query = "SELECT * FROM `menu_type` ORDER BY id ASC";        $db = new Database();        $result = $db->readQuery($query);        $array_res = array();        while ($row = mysqli_fetch_array($result)) {            array_push($array_res, $row);        }        return $array_res;    }    public function update() {        $query = "UPDATE  `menu_type` SET "                . "`title` ='" . $this->title . "', "                . "`queue` ='" . $this->queue . "' "                . "WHERE `id` = '" . $this->id . "'";        $db = new Database();        $result = $db->readQuery($query);        if ($result) {            return $this->__construct($this->id);        } else {            return FALSE;        }    }    public function delete() {        $query = 'DELETE FROM `menu_type` WHERE id="' . $this->id . '"';        $db = new Database();        return $db->readQuery($query);    }    public function arrange($key, $img) {        $query = "UPDATE `menu_type` SET `queue` = '" . $key . "'  WHERE id = '" . $img . "'";        $db = new Database();        $result = $db->readQuery($query);        return $result;    }}