<?php/* * To change this license header, choose License Headers in Project Properties. * To change this template file, choose Tools | Templates * and open the template in the editor. *//** * Description of OfferPhoto * * @author mytravelpartner.lk */class OfferDuration {    public $id;    public $title;    public $dates;    public $price;     public function __construct($id) {        if ($id) {            $query = "SELECT * FROM `offer_duration` WHERE `id`=" . $id;            $db = new Database();            $result = mysqli_fetch_array($db->readQuery($query));            $this->id = $result['id'];            $this->title = $result['title'];            $this->dates = $result['dates'];            $this->price = $result['price'];             return $this;        }    }    public function create() {        $query = "INSERT INTO `offer_duration` (`title`,`dates`,`price`) VALUES  ('"                . $this->title . "','"                . $this->dates . "', '"                 . $this->price . "')";        $db = new Database();        $result = $db->readQuery($query);        if ($result) {            $last_id = mysqli_insert_id();            return $this->__construct($last_id);        } else {            return FALSE;        }    }    public function all() {        $query = "SELECT * FROM `offer_duration` ";        $db = new Database();        $result = $db->readQuery($query);        $array_res = array();        while ($row = mysqli_fetch_array($result)) {            array_push($array_res, $row);        }        return $array_res;    }    public function update() {        $query = "UPDATE  `offer_duration` SET "                . "`title` ='" . $this->title . "', "                . "`dates` ='" . $this->dates . "', "                . "`price` ='" . $this->price . "' "                 . "WHERE `id` = '" . $this->id . "'";        $db = new Database();        $result = $db->readQuery($query);        if ($result) {            return $this->__construct($this->id);        } else {            return FALSE;        }    }    public function delete() {        $query = 'DELETE FROM `offer_duration` WHERE id="' . $this->id . '"';        $db = new Database();        return $db->readQuery($query);    }       public function arrange($key, $img) {        $query = "UPDATE `offer_duration` SET `queue` = '" . $key . "'  WHERE id = '" . $img . "'";        $db = new Database();        $result = $db->readQuery($query);        return $result;    }}