];            $this->budget = $result['budget'];            $this->air_condition = $result['air_condition'];            $this->contact_name = $result['contact_name'];            $this->transmission = $result['transmission'];            $this->num_doors = $result['num_doors'];            $this->num_passengers = $result['num_passengers'];            $this->num_baggaes = $result['num_baggaes'];            $this->city = $result['city'];            $this->airport_transfer = $result['airport_transfer'];            $this->wedding_cars = $result['wedding_cars'];            $this->description = $result['description'];            $this->status = $result['status'];            $this->queue = $result['queue'];            return $this;        }    }    public function create() {        $query = "INSERT INTO `rent_car` (`customer_id`,`company_id`,`type`,`title`,`reg_number`,`reg_year`,`fuel_type`,`email`,`contact_name` ,`contact_number`,`contact_number_2`,`main_image`,`driving_license_front`,`driving_license_back`,"                . "`city`,`budget`, `air_condition`,`transmission`,`num_doors`,`num_passengers`,`num_baggaes`, `airport_transfer`,`wedding_cars`,`description`,`status`,`queue`) VALUES  ('"                . $this->customer_id . "','"                . $this->company_id . "','"                . $this->type . "','"                . $this->title . "','"                . $this->reg_number . "','"                . $this->reg_year . "','"                . $this->fuel_type . "','"                . $this->email . "','"                . $this->contact_name . "','"                . $this->contact_number . "','"                . $this->contact_number_2 . "','"                . $this->main_image . "','"                . $this->driving_license_front . "','"                . $this->driving_license_back . "','"                . $this->city . "','"                . $this->budget . "','"                . $this->air_condition . "','"                . $this->transmission . "','"                . $this->num_doors . "','"                . $this->num_passengers . "','"                . $this->num_baggaes . "','"                . $this->airport_transfer . "', '"                . $this->wedding_cars . "', '"                . $this->description . "', '"                . $this->status . "', '"                . $this->queue . "')";        $db = new Database();        $result = $db->readQuery($query);        if ($result) {            $last_id = mysqli_insert_id();            return $this->__construct($last_id);        } else {            return FALSE;        }    }    public function all() {        $query = "SELECT * FROM `rent_car` ORDER BY queue ASC";        $db = new Database();        $result = $db->readQuery($query);        $array_res = array();        while ($row = mysqli_fetch_array($result)) {            array_push($array_res, $row);        }        return $array_res;    }    public function getAllByFilter($rent_car_type, $air_condition, $feature, $district, $pageLimit, $setLimit) {        $query = "SELECT DISTINCT(rent_car.id),rent_car.main_image,rent_car.title,rent_car.city,rent_car.description,rent_car.num_doors,rent_car.num_passengers,rent_car.num_baggaes  FROM `rent_car` JOIN rent_a_car_feature ON rent_a_car_feature.rent_a_car=rent_car.id WHERE ";        if (!empty($rent_car_type)) {            $rent_car_type_filter = implode(",", $rent_car_type);            $query .= "rent_car.type in( " . $rent_car_type_filter . ")  AND ";        }        if (!empty($air_condition)) {            $air_condition_filter = implode(",", $air_condition);            $query .= "rent_car.air_condition in( " . $air_condition_filter . ")  AND ";        }        if (!empty($feature)) {            $feature_filter = implode(",", $feature);            $query .= "rent_a_car_feature.feature in( " . $feature_filter . ")  AND ";        }        if (!empty($districts)) {            $district_filter = implode(",", $districts);            $query .= "rent_car.district in( " . $district_filter . ")  AND ";        }        $query .= "rent_car.status = 1  ORDER BY rent_car.queue ASC LIMIT " . $pageLimit . " , " . $setLimit;                $db = new Database();        $result = $db->readQuery($query);        $out_put = '';        $out_put .= '';        while ($row = mysqli_fetch_array($result)) {            $RENT_CARS = new RentCar(NULL);            $RENT_CAR_PRICE = new RentCarPrice(NULL);            $DEFULTDATA = new DefaultData(NULL);            $out_put .= ' <div class="product-long-item">                                                <div class="row equal-height shrink-auto-md gap-15">                                                    <div class="col-12 col-shrink">                                                       <a href="view-rent-car-package.php?id=' . $row['id'] . '" >                                                        <div class="image height-auto">                                                            <img src="upload/rent-a-car/gallery/thumb/' . $row['main_image'] . '" alt="' . $row['title'] . '" />                                                        </div>                                                        </a>                                                    </div>                                                    <div class="col-12 col-auto">                                                        <div class="col-inner d-flex flex-column">                                                            <div   lass="mb-5">                                                                <div class="d-flex">                                                                    <div>                                                                        <div class="rating-item rating-sm">                                                                            <div class="rating-icons">                                                                                <p class="rating-text text-muted">        <span class="material-icons star-size-1">    star   </span> <span class="bg-primary">6.0</span> <strong class="text-dark">Good</strong> <span class="font13">- 1,2547 reviews</span></p>                                                                              </div>                                                                        </div>                                                                        <h5><a href="view-rent-car-package.php?id=' . $row['id'] . '">' . $row['title'] . '</a></h5>                                                                        <p class = "location"><i class = "fas fa-map-marker-alt text-primary"></i>' . substr($row['city'], 0, 40) . '..</p>                                                                    </div>                                                                    <div class="ml-auto">                                                                        <div class="price ">                                                                            start from';            $min_price = $RENT_CAR_PRICE->getMinPriceByCompany($row['id']);            foreach ($min_price as $min) {                $out_put .= ' <span class="text-secondary f-14">';                foreach ($DEFULTDATA->GetCurrancyType() as $key => $curancy) {                    if ($min['currency_type'] == $key) {                        $out_put .= $curancy . ' ' . $min['min_prce'] . '</span>';                    }                }            }            $out_put .= '  per package                                                                        </div>                                                                      </div>                                                                </div>                                                            </div>                                                            <div class="mb-5 mt-5">                                                               ' . substr($row['description'], 0, 115) . '...                                                             </div>                                                            <div class="content-bottom mt-auto">                                                                <div class="d-flex align-items-center">                                                                    <div>                                                                        <ul class="meta-list">';                                 $out_put .= '<li>' . $row['num_doors'] . ' doors</li>'                            . ' <li>' . $row['num_passengers'] . ' seats</li>'                            . ' <li>' . $row['num_baggaes'] . ' baggages</li>'  ;                            $out_put .= ' </ul>                                                                    </div>                                                                    <div class="ml-auto">                                                                        <a href="view-rent-car-package.php?id=' . $row['id'] . '" class="btn btn-primary btn-sm btn-wide">Details</a>                                                                    </div>                                                                </div>                                                            </div>                                                        </div>                                                    </div>                                                </div>                                                </div> ';        }        $out_put .= '';        if (!empty($out_put)) {            echo $out_put;        } else {            echo $out_put = '<p style = "color: red;font-weight: 600;">No Data Found..!</p>';        }    }    public function showPagination($per_page, $page) {        $query = "SELECT COUNT(*) as totalCount FROM `rent_car` WHERE   `status` = 1  ORDER BY `queue` asc";        $db = new Database();        $page_url = "?";        $rec = mysqli_fetch_array(mysql_query($query));        $total = $rec['totalCount'];        $adjacents = "2";        $page = ($page == 0 ? 1 : $page);        $start = ($page - 1) * $per_page;        $first = $start + 1;        $last = $start + $per_page;        $prev = $page - 1;        $next = $page + 1;        $setLastpage = ceil($total / $per_page);        $lpm1 = $setLastpage - 1;        $setPaginate = "";        $setPaginate .= "<div class='setPage'>Showing result $first to $last from $total</div>";        if ($setLastpage > 1) {            $setPaginate .= "<ul class='setPaginate'>";            if ($page > 1) {                $setPaginate.= "<li><a href='#' class='page' page='$prev'>&laquo;</a></li>";            } else {                $setPaginate.= "<li><a class='current_page1 page'>&laquo;</a></li>";            }            if ($setLastpage < 7 + ($adjacents * 2)) {                for ($counter = 1; $counter <= $setLastpage; $counter++) {                    if ($counter == $page)                        $setPaginate.= "<li><a class='current_page  '>$counter</a></li>";                    else                        $setPaginate.= "<li><a href='#' class='page' page='$counter'>$counter</a></li>";                }            }            elseif ($setLastpage > 5 + ($adjacents * 2)) {                if ($page < 1 + ($adjacents * 2)) {                    for ($counter = 1; $counter < 4 + ($adjacents * 2); $counter++) {                        if ($counter == $page)                            $setPaginate.= "<li><a class='current_page page'>$counter</a></li>";                        else                            $setPaginate.= "<li><a href='#' class='page' page='$counter'>$counter</a></li>";                    }                    $setPaginate.= "<li class='dot'>...</li>";                    $setPaginate.= "<li><a href='#' class='page' page='$lpm1'>$lpm1</a></li>";                    $setPaginate.= "<li><a href='#' class='page' page='$setLastpage'>$setLastpage</a></li>";                }                elseif ($setLastpage - ($adjacents * 2) > $page && $page > ($adjacents * 2)) {                    $setPaginate.= "<li><a href='#' class='page' page='1'>1</a></li>";                    $setPaginate.= "<li><a href='#' class='page' page='2'>2</a></li>";                    $setPaginate.= "<li class='dot'>...</li>";                    for ($counter = $page - $adjacents; $counter <= $page + $adjacents; $counter++) {                        if ($counter == $page)                            $setPaginate.= "<li><a class='current_page page'>$counter</a></li>";                        else                            $setPaginate.= "<li><a href='#' class='page' page='$counter'>$counter</a></li>";                    }                    $setPaginate.= "<li class='dot'>..</li>";                    $setPaginate.= "<li><a href='#' class='page' page='$lpm1'>$lpm1</a></li>";                    $setPaginate.= "<li><a href='#' class='page' page='$setLastpage'>$setLastpage</a></li>";                }                else {                    $setPaginate.= "<li><a href='#' class='page' page='1>1</a></li>";                    $setPaginate.= "<li><a href='#' class='page' page='2'>2</a></li>";                    $setPaginate.= "<li class='dot'>..</li>";                    for ($counter = $setLastpage - (2 + ($adjacents * 2)); $counter <= $setLastpage; $counter++) {                        if ($counter == $page)                            $setPaginate.= "<li><a class='current_page page'>$counter</a></li>";                        else                            $setPaginate.= "<li><a href='#' class='page' page='$counter'>$counter</a></li>";                    }                }            }            if ($page < $counter - 1) {                $setPaginate.= "<li><a href='#' class='page' page='$next'>&raquo;</a></li>";            } else {                $setPaginate.= "<li><a class='current_page1 page'>&raquo;</a></li>";            }            $setPaginate.= "</ul>\n";        }        echo $setPaginate;    }    public function getCarsByCompany($id) {        $query = "SELECT * FROM `rent_car` WHERE `company_id`= $id ORDER BY queue ASC";        $db = new Database();        $result = $db->readQuery($query);        $array_res = array();        while ($row = mysqli_fetch_array($result)) {            array_push($array_res, $row);        }        return $array_res;    }    public function update() {        $query = "UPDATE  `rent_car` SET "                . "`title` ='" . $this->title . "', "                . "`type` ='" . $this->type . "', "                . "`reg_number` ='" . $this->reg_number . "', "                . "`reg_year` ='" . $this->reg_year . "', "                . "`fuel_type` ='" . $this->fuel_type . "', "                . "`contact_name` ='" . $this->contact_name . "', "                . "`contact_number` ='" . $this->contact_number . "', "                . "`contact_number_2` ='" . $this->contact_number_2 . "', "                . "`main_image` ='" . $this->main_image . "', "                . "`driving_license_front` ='" . $this->driving_license_front . "', "                . "`driving_license_back` ='" . $this->driving_license_back . "', "                . "`city` ='" . $this->city . "', "                . "`budget` ='" . $this->budget . "', "                . "`air_condition` ='" . $this->air_condition . "', "                . "`contact_name` ='" . $this->contact_name . "', "                . "`transmission` ='" . $this->transmission . "', "                . "`num_doors` ='" . $this->num_doors . "', "                . "`airport_transfer` ='" . $this->airport_transfer . "', "                . "`wedding_cars` ='" . $this->wedding_cars . "', "                . "`description` ='" . $this->description . "', "                . "`queue` ='" . $this->queue . "' "                . "WHERE `id` = '" . $this->id . "'";        $db = new Database();        $result = $db->readQuery($query);        if ($result) {            return $this->__construct($this->id);        } else {            return FALSE;        }    }    public function updateLocation() {        $query = "UPDATE  `rent_car` SET "                . "`description` ='" . $this->description . "', "                . "`map` ='" . $this->map . "', "                . "`location` ='" . $this->location . "', "                . "`isPublished` ='" . $this->isPublished . "' "                . "WHERE `id` = '" . $this->id . "'";        $db = new Database();        $result = $db->readQuery($query);        if ($result) {            return $this->__construct($this->id);        } else {            return FALSE;        }    }      public function delete() {        $this->deletePhotos();        $RENT_CAR_PRICE = new RentCarPrice(NULL);        $RENT_CAR_PRICE->deleteAll($this->id);        $query = 'DELETE FROM `rent_car` WHERE id = "' . $this->id . '"';        $db = new Database();        return $db->readQuery($query);    }    public function deletePhotos() {        $RENT_CAR_PHOTO = new RentCarPhoto(NULL);        $allPhotos = $RENT_CAR_PHOTO->getCarPhotosById($this->id);        foreach ($allPhotos as $photo) {            $IMG = $RENT_CAR_PHOTO->image_name = $photo["image_name"];            unlink(Helper::getSitePath() . "upload/rent-a-car/gallery/" . $IMG);            unlink(Helper::getSitePath() . "upload/rent-a-car/gallery/thumb/" . $IMG);            $RENT_CAR_PHOTO->id = $photo["id"];            $RENT_CAR_PHOTO->delete();        }    }    public function arrange($key, $img) {        $query = "UPDATE `rent_car` SET `queue` = '" . $key . "'  WHERE 