<?php/* * To change this license header, choose License Headers in Project Properties. * To change this template file, choose Tools | Templates * and open the template in the editor. *//** * Description of Room_photo * * @author Chalana  */class RentCarFeature {    public $id;    public $rent_a_car;    public $feature;    public $queue;    public function __construct($id) {        if ($id) {            $query = "SELECT * FROM `rent_a_car_feature` WHERE `id`=" . $id;            $db = new Database();            $result = mysqli_fetch_array($db->readQuery($query));            $this->id = $result['id'];            $this->rent_a_car = $result['rent_a_car'];            $this->feature = $result['feature'];            $this->queue = $result['queue'];            return $this;        }    }    public function create() {        $query = "INSERT INTO `rent_a_car_feature` ( `rent_a_car`,`feature`, `queue`) VALUES  ('"                . $this->rent_a_car . "','"                . $this->feature . "', '"                . $this->queue . "')";        $db = new Database();        $result = $db->readQuery($query);        if ($result) {            $last_id = mysqli_insert_id();            return $this->__construct($last_id);        } else {            return FALSE;        }    }    public function all() {        $query = "SELECT * FROM `rent_a_car_feature` ORDER BY queue ASC";        $db = new Database();        $result = $db->readQuery($query);        $array_res = array();        while ($row = mysqli_fetch_array($result)) {            array_push($array_res, $row);        }        return $array_res;    }    public function update() {        $query = "UPDATE  `rent_a_car_feature` SET "                . "`rent_a_car` ='" . $this->rent_a_car . "', "                . "`feature` ='" . $this->feature . "', "                . "`queue` ='" . $this->queue . "' "                . "WHERE `id` = '" . $this->id . "'";        $db = new Database();        $result = $db->readQuery($query);        if ($result) {            return $this->__construct($this->id);        } else {            return FALSE;        }    }    public function delete() {        $query = 'DELETE FROM `rent_a_car_feature` WHERE id="' . $this->id . '"';        $db = new Database();        return $db->readQuery($query);    }    public function deleteCarFeature($id, $car_id) {        $query = 'DELETE FROM `rent_a_car_feature` WHERE `feature`= "' . $id . '" AND rent_a_car="' . $car_id . '"';        $db = new Database();        return $db->readQuery($query);    }    public function deleteCar() {        $query = 'DELETE FROM `rent_a_car_feature` WHERE id="' . $this->id . '"';        $db = new Database();        return $db->readQuery($query);    }    public function getFeaturesById($rent_a_car) {        $query = "SELECT * FROM `rent_a_car_feature` WHERE `rent_a_car`= $rent_a_car ORDER BY queue ASC";        $db = new Database();        $result = $db->readQuery($query);        $array_res = array();        while ($row = mysqli_fetch_array($result)) {            array_push($array_res, $row);        }        return $array_res;    }    public function getRoomPhotosByAccId($acc_id) {        $query = "SELECT * FROM `rent_a_car_feature` WHERE `acc_id`= $acc_id ORDER BY queue ASC";        $db = new Database();        $result = $db->readQuery($query);        $array_res = array();        while ($row = mysqli_fetch_array($result)) {            array_push($array_res, $row);        }        return $array_res;    }    public function arrange($key, $img) {        $query = "UPDATE `rent_a_car_feature` SET `queue` = '" . $key . "'  WHERE id = '" . $img . "'";        $db = new Database();        $result = $db->readQuery($query);        return $result;    }}