<?php/* * To change this license header, choose License Headers in Project Properties. * To change this template file, choose Tools | Templates * and open the template in the editor. *//** * Description of Room_photo * * @author Chalana  */class RentCarPhoto {    public $id;    public $rent_car;    public $image_name;    public $queue;    public function __construct($id) {        if ($id) {            $query = "SELECT * FROM `rent_car_photo` WHERE `id`=" . $id;            $db = new Database();            $result = mysqli_fetch_array($db->readQuery($query));            $this->id = $result['id'];            $this->rent_car = $result['rent_car'];            $this->image_name = $result['image_name'];            $this->queue = $result['queue'];            return $this;        }    }    public function create() {        $query = "INSERT INTO `rent_car_photo` ( `rent_car`,`image_name`, `queue`) VALUES  ('"                . $this->rent_car . "','"                . $this->image_name . "', '"                . $this->queue . "')";        $db = new Database();        $result = $db->readQuery($query);        if ($result) {            $last_id = mysqli_insert_id();            return $this->__construct($last_id);        } else {            return FALSE;        }    }    public function all() {        $query = "SELECT * FROM `rent_car_photo` ORDER BY queue ASC";        $db = new Database();        $result = $db->readQuery($query);        $array_res = array();        while ($row = mysqli_fetch_array($result)) {            array_push($array_res, $row);        }        return $array_res;    }    public function update() {        $query = "UPDATE  `rent_car_photo` SET "                . "`rent_car` ='" . $this->rent_car . "', "                . "`image_name` ='" . $this->image_name . "', "                . "`queue` ='" . $this->queue . "' "                . "WHERE `id` = '" . $this->id . "'";        $db = new Database();        $result = $db->readQuery($query);        if ($result) {            return $this->__construct($this->id);        } else {            return FALSE;        }    }    public function delete() {                $query = 'DELETE FROM `rent_car_photo` WHERE id="' . $this->id . '"';        $db = new Database();        return $db->readQuery($query);    }       public function getCarPhotosById($rent_car) {        $query = "SELECT * FROM `rent_car_photo` WHERE `rent_car`= $rent_car ORDER BY queue ASC";        $db = new Database();        $result = $db->readQuery($query);        $array_res = array();        while ($row = mysqli_fetch_array($result)) {            array_push($array_res, $row);        }        return $array_res;    }    public function getRoomPhotosByAccId($acc_id) {        $query = "SELECT * FROM `rent_car_photo` WHERE `acc_id`= $acc_id ORDER BY queue ASC";        $db = new Database();        $result = $db->readQuery($query);        $array_res = array();        while ($row = mysqli_fetch_array($result)) {            array_push($array_res, $row);        }        return $array_res;    }    public function arrange($key, $img) {        $query = "UPDATE `rent_car_photo` SET `queue` = '" . $key . "'  WHERE id = '" . $img . "'";        $db = new Database();        $result = $db->readQuery($query);        return $result;    }}