<?php/* * To change this license header, choose License Headers in Project Properties. * To change this template file, choose Tools | Templates * and open the template in the editor. *//** * Description of rent_car_price * * @author Chalana Dulaj */class RentCarPrice {    public $id;    public $rent_car;    public $currency_type;    public $type;    public $driver;    public $price;    public $extra_price;    public function __construct($id) {        if ($id) {            $query = "SELECT * FROM `rent_car_price` WHERE `id`=" . $id;            $db = new Database();            $result = mysqli_fetch_array($db->readQuery($query));            $this->id = $result['id'];            $this->rent_car = $result['rent_car'];            $this->currency_type = $result['currency_type'];            $this->type = $result['type'];            $this->driver = $result['driver'];            $this->price = $result['price'];            $this->extra_price = $result['extra_price'];            return $this;        }    }    public function create() {        $query = "INSERT INTO `rent_car_price` (`rent_car`,`currency_type`,`type`,`driver`,`price`,`extra_price` ) VALUES  ('"                . $this->rent_car . "','"                . $this->currency_type . "','"                . $this->type . "','"                . $this->driver . "', '"                . $this->price . "', '"                . $this->extra_price . "')";        $db = new Database();        $result = $db->readQuery($query);        if ($result) {            $last_id = mysqli_insert_id();            return $this->__construct($last_id);        } else {            return FALSE;        }    }    public function all() {        $query = "SELECT * FROM `rent_car_price` ORDER BY id ASC";        $db = new Database();        $result = $db->readQuery($query);        $array_res = array();        while ($row = mysqli_fetch_array($result)) {            array_push($array_res, $row);        }        return $array_res;    }    public function getCarsPriceByCar($id) {        $query = "SELECT * FROM `rent_car_price` WHERE `rent_car`= $id ORDER BY id ASC";        $db = new Database();        $result = $db->readQuery($query);        $array_res = array();        while ($row = mysqli_fetch_array($result)) {            array_push($array_res, $row);        }        return $array_res;    }    public function getMinPriceByCompany($id) {        $query = "SELECT min(price) as 'min_prce',`currency_type` FROM `rent_car_price` WHERE `rent_car` = '" . $id . "' ";        $db = new Database();        $result = $db->readQuery($query);        $array_res = array();        while ($row = mysqli_fetch_array($result)) {            array_push($array_res, $row);        }        return $array_res;    }     public function update() {        $query = "UPDATE  `rent_car_price` SET "                . "`rent_car` ='" . $this->rent_car . "', "                . "`currency_type` ='" . $this->currency_type . "', "                . "`type` ='" . $this->type . "', "                . "`driver` ='" . $this->driver . "', "                . "`price` ='" . $this->price . "', "                . "`extra_price` ='" . $this->extra_price . "' "                . "WHERE `id` = '" . $this->id . "'";        $db = new Database();        $result = $db->readQuery($query);        if ($result) {            return $this->__construct($this->id);        } else {            return FALSE;        }    }    public function delete() {        $query = 'DELETE FROM `rent_car_price` WHERE id="' . $this->id . '"';        $db = new Database();        return $db->readQuery($query);    }    public function deleteAll($id) {        $query = 'DELETE FROM `rent_car_price` WHERE rent_car="' . $id . '"';         $db = new Database();        return $db->readQuery($query);    }    public function getActivitiesByTitle($rent_car) {        $query = "SELECT `id` FROM `rent_car_price` WHERE `rent_car` LIKE '" . $rent_car . "'";        $db = new Database();        $result = mysqli_fetch_array($db->readQuery($query));        return $result['id'];    }}