<?php/* * To change this license header, choose License Headers in Project Properties. * To change this template file, choose Tools | Templates * and open the template in the editor. *//** * Description of Tour_dates_photos * * @author chalana */class TourPackagePhoto {    public $id;    public $tour_package;    public $image_name;    public $caption;    public $queue;    public function __construct($id) {        if ($id) {            $query = "SELECT * FROM `tour_package_photo` WHERE `id`=" . $id;            $db = new Database();            $result = mysqli_fetch_array($db->readQuery($query));            $this->id = $result['id'];            $this->tour_package = $result['tour_package'];            $this->image_name = $result['image_name'];            $this->caption = $result['caption'];            $this->queue = $result['queue'];            return $this;        }    }    public function create() {        $query = "INSERT INTO `tour_package_photo` (`tour_package`,`image_name`,`caption`,`queue`) VALUES  ('"                . $this->tour_package . "','"                . $this->image_name . "', '"                . $this->caption . "', '"                . $this->queue . "')";              $db = new Database();        $result = $db->readQuery($query);        if ($result) {            $last_id = mysqli_insert_id();            return $this->__construct($last_id);        } else {            return FALSE;        }    }    public function all() {        $query = "SELECT * FROM `tour_package_photo` ORDER BY queue ASC";        $db = new Database();        $result = $db->readQuery($query);        $array_res = array();        while ($row = mysqli_fetch_array($result)) {            array_push($array_res, $row);        }        return $array_res;    }    public function update() {        $query = "UPDATE  `tour_package_photo` SET "                . "`tour_package` ='" . $this->tour_package . "', "                . "`image_name` ='" . $this->image_name . "', "                . "`caption` ='" . $this->caption . "', "                . "`queue` ='" . $this->queue . "' "                . "WHERE `id` = '" . $this->id . "'";        $db = new Database();        $result = $db->readQuery($query);        if ($result) {            return $this->__construct($this->id);        } else {            return FALSE;        }    }    public function delete() {        unlink(Helper::getSitePath() . "upload/tour-package/gallery/" . $this->image_name);        unlink(Helper::getSitePath() . "upload/tour-package/gallery/thumb/" . $this->image_name);        $query = 'DELETE FROM `tour_package_photo` WHERE id="' . $this->id . '"';        $db = new Database();        return $db->readQuery($query);    }    public function getTourPackagePhotosByPackage($id) {        $query = "SELECT * FROM `tour_package_photo` WHERE `tour_package`= '" . $id . "'   ORDER BY queue ASC";        $db = new Database();        $result = $db->readQuery($query);        $array_res = array();        while ($row = mysqli_fetch_array($result)) {            array_push($array_res, $row);        }        return $array_res;    }    public function arrange($key, $img) {        $query = "UPDATE `tour_package_photo` SET `queue` = '" . $key . "'  WHERE id = '" . $img . "'";        $db = new Database();        $result = $db->readQuery($query);        return $result;    }}